function go_new_num() {
    //    Самооценка
    document.querySelector('.d_p3').innerHTML = calc_new_num('.kv_1', '.kv_4', '.kv_7');
    //    Талант 
    document.querySelector('.d_p2').innerHTML = calc_new_num('.kv_3', '.kv_6', '.kv_9');
    //    Духовность 
    document.querySelector('.d_p1').innerHTML = calc_new_num('.kv_1', '.kv_5', '.kv_9');
    //    Быт
    document.querySelector('.d_p4').innerHTML = calc_new_num('.kv_2', '.kv_5', '.kv_8');
}

function calc_new_num(a, b, c) {
    let num1, num2, num3;

    num1 = document.querySelector(a).innerHTML;
    num1.trim();
    num1 = check(num1);

    num2 = document.querySelector(b).innerHTML;
    num2.trim();
    num2 = check(num2);

    num3 = document.querySelector(c).innerHTML;
    num3.trim();
    num3 = check(num3);


    function check(num) {

        if (num == 'Пусто') {
            return ''
        } else {
            return num
        }

    }

    let my_out = num1.length + num2.length + num3.length

    return my_out

}